import Carrousel from "../components/Carrousel"
import styled from 'styled-components'
import colors from '../utils/style/color'
import { Component } from "react"
import Dropdown from "../components/Dropdown"
import Tags from "../components/Tags"
import '../styles/Lodging.css'

const HostName = styled.h4`
    color: ${colors.primary};
    font-size: 18px;
    text-align: right;
    width: 70px;
`

const HostPicture = styled.img`
    border-radius: 50%;
    width: 64px;
    height: 64px;
    margin-left: 2em;
`

const LodgingH2 = styled.h2`
    font-size: 18px;
    color: ${colors.primary};
    margin-top: -5px;
`
const InfosSection = styled.section`
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(auto, 580px));
    gap: 76px;
`

const EquipmentsUl = styled.ul`
    list-style: none;
    padding: 0;
`
const TagsStar = styled.div`
    display: flex;
    justify-content: space-between;
`

const StarDiv = styled.div`
    color: ${colors.primary};
    font-size: 36px;
`

class Lodging extends Component{
    constructor(props) {
    super(props)
    this.state = {
        lodgingId: this.props.match.params.lodgingId,
        lodgingData: [],
        rating: [
        <StarDiv><i class="far fa-star"></i> <i class="far fa-star"></i> <i class="far fa-star"></i> <i class="far fa-star"></i> <i class="far fa-star"></i></StarDiv>,
        <StarDiv><i class="fas fa-star"></i> <i class="far fa-star"></i> <i class="far fa-star"></i> <i class="far fa-star"></i> <i class="far fa-star"></i></StarDiv>,
        <StarDiv><i class="fas fa-star"></i> <i class="fas fa-star"></i> <i class="far fa-star"></i> <i class="far fa-star"></i> <i class="far fa-star"></i></StarDiv>,
        <StarDiv><i class="fas fa-star"></i> <i class="fas fa-star"></i> <i class="fas fa-star"></i> <i class="far fa-star"></i> <i class="far fa-star"></i></StarDiv>,
        <StarDiv><i class="fas fa-star"></i> <i class="fas fa-star"></i> <i class="fas fa-star"></i> <i class="fas fa-star"></i> <i class="far fa-star"></i></StarDiv>,
        <StarDiv><i class="fas fa-star"></i> <i class="fas fa-star"></i> <i class="fas fa-star"></i> <i class="fas fa-star"></i> <i class="fas fa-star"></i></StarDiv>]
    }
}

componentDidMount() {
    // const { id } = this.props.match.params

    fetch(`../logements.json`)
    .then((response) => response.json())
    .then((jsonResponse) => {
        this.setState({ lodgingData: jsonResponse.find(e => e.id === this.state.lodgingId) })
        console.log(this.state.lodgingData)
    })
}

    render(){    
        const lodgingData = this.state.lodgingData
        const Equipments = this.state.lodgingData.equipments
        const Host = this.state.lodgingData.host
        const rating = this.state.rating
        console.log(rating)
        // let userId = this.props.match.params.lodgingId;
    return(
        <main>
            <Carrousel imgs={lodgingData.pictures}/>
            <section className='lodging__title'>
                <h1>{lodgingData.title}</h1>
                <div className='lodging__title__details'>
                    {Host && <><HostName>{Host.name}</HostName> <HostPicture alt='host' src={`${Host.picture}`} /></>}
                </div>
            </section>
            <LodgingH2>{lodgingData.location}</LodgingH2>
            <TagsStar className='tags__star'>
                <Tags tags= {lodgingData.tags}/>
                {rating[lodgingData.rating]}
            </TagsStar>
            <InfosSection>
            <Dropdown title={`Description`} details={lodgingData.description}/>
            <Dropdown title={`Équipements`} details={<EquipmentsUl>{Equipments && Equipments.map(equipment => <li key={`equipment-${equipment}`}>{equipment}</li>)}</EquipmentsUl>}/>
            </InfosSection>
        </main>
    )}
}

export default Lodging
