import '../styles/Dropdown.css'
import styled from 'styled-components'

const DropdownTitle = styled.span`
    color: #fff;
    background-color: #ff6060;
    border-radius: 5px;
    font-size: 24px;
    font-weight: 500;
    height: 47px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding-left: 18px;
    padding-right: 18px;
    z-index: 10;
`


let chevronDown = <i className="fas fa-chevron-down d-block"></i>
let chevronUp = <i className="fas fa-chevron-up d-none"></i>

function Dropdown({title, details}){
    return (
        <div className="dropdown">
            <DropdownTitle className="dropdown__title">
                <h3>{title}</h3>
                {chevronDown}
                {chevronUp}
            </DropdownTitle>
            <p className="dropdown__details d-none">
                {details}
            </p>
        </div>
    )
}

export default Dropdown
