import '../styles/Dropdown.css'

let chevronDown = <i class="fas fa-chevron-down d-block"></i>
let chevronUp = <i class="fas fa-chevron-up d-none"></i>

function Dropdown({title, details}){
    function detailsVisible(){
        chevronDown = <i class="fas fa-chevron-down d-none"></i>
        chevronUp = <i class="fas fa-chevron-up d-block"></i>
    }
    return (
        <div className="dropdown">
            <div className="dropdown__title">
                <h3>{title}</h3>
                {chevronDown}
                {chevronUp}
            </div>
            <p className="dropdown__details d-none">
                {details}
            </p>
        </div>
    )
}

export default Dropdown
