//     console.log(imgs)
// }

    render(){

        const {imgs} = this.props
        // const {showRoom} = imgs.map((img) => (<img className='carrousel__img' src={img} alt='location'></img>))
        // console.log(imgs)
        return (
    <div className='carrousel'>
        {imgs.for(img => (<img className='carrousel__img' src={img} alt='location'></img>))}
        <i className="fas fa-chevron-right"></i>
        <i className="fas fa-chevron-left"></i>
        <p className='carrousel__img__number'>1/4</p>
    </div>)
    }
}

export default Carrousel
