import Carrousel from "../components/Carrousel"
import { Component } from "react"

class Lodging extends Component{
    constructor(props) {
    super(props)
    this.state = {
        lodgingId: this.props.match.params.lodgingId,
        lodgingData: []
    }
}

componentDidMount() {
    // const { id } = this.props.match.params

    fetch(`../logements.json`)
    .then((response) => response.json())
    .then((jsonResponse) => {
        this.setState({ logementData: jsonResponse.find(e => e.id === this.state.lodgingId) })
        console.log(this.state.logementData)
    })
}

    render(){    

        // let userId = this.props.match.params.lodgingId;
    return(
        <main>
            <Carrousel />
            <h1>{this.state.lodgingData.title}</h1>
            <h2>{this.state.lodgingData.location}</h2>
            <ul>
                {this.state.lodgingData.tags.map((tag) => (<li>{tag}</li>))}
            </ul>
        </main>
    )}
}

export default Lodging
