import Carrousel from "../components/Carrousel"
import styled from 'styled-components'
import colors from '../utils/style/color'
import { Component } from "react"
import Dropdown from "../components/Dropdown"
import Tags from "../components/Tags"

const LodgingH1 = styled.h1`
    font-size: 36px;
    color: ${colors.primary};
`

const LodgingH2 = styled.h2`
    font-size: 18px;
    color: ${colors.primary};
`
const InfosSection = styled.section`
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(auto, 580px));
    gap: 76px;
`

const EquipmentsUl = styled.ul`
    list-style: none;
    padding: 0;
`
const TagsStar = styled.div`
    display: flex;
    justify-content: space-between;
`    

class Lodging extends Component{
    constructor(props) {
    super(props)
    this.state = {
        lodgingId: this.props.match.params.lodgingId,
        lodgingData: [],
        rating: {
            1: `<i class="fas fa-star"><i class="far fa-star"></i><i class="far fa-star"></i><i class="far fa-star"></i><i class="far fa-star"></i>`,
            2: `<i class="fas fa-star"></i><i class="fas fa-star"></i><i class="far fa-star"></i><i class="far fa-star"></i><i class="far fa-star"></i>`,
            3: `<i class="fas fa-star"></i><i class="fas fa-star"></i><i class="fas fa-star"></i><i class="far fa-star"></i><i class="far fa-star"></i>`,
            4: `<i class="fas fa-star"></i><i class="fas fa-star"></i><i class="fas fa-star"></i><i class="fas fa-star"></i><i class="far fa-star"></i>`,
            5: `<i class="fas fa-star"></i><i class="fas fa-star"></i><i class="fas fa-star"></i><i class="fas fa-star"></i><i class="fas fa-star"></i>`
        }
    }
}

componentDidMount() {
    // const { id } = this.props.match.params

    fetch(`../logements.json`)
    .then((response) => response.json())
    .then((jsonResponse) => {
        this.setState({ lodgingData: jsonResponse.find(e => e.id === this.state.lodgingId) })
        console.log(this.state.lodgingData)
    })
}

    render(){    
        const lodgingData = this.state.lodgingData
        const Equipments = this.state.lodgingData.equipments
        console.log(Equipments)
        // let userId = this.props.match.params.lodgingId;
    return(
        <main>
            <Carrousel imgs={lodgingData.pictures}/>
            <LodgingH1>{lodgingData.title}</LodgingH1>
            <LodgingH2>{lodgingData.location}</LodgingH2>
            <TagsStar className='tags__star'>
                <Tags tags= {lodgingData.tags}/>
                <div><i class="fas fa-star"></i> * {lodgingData.rating}</div>
            </TagsStar>
            <InfosSection>
            <Dropdown title={`Description`} details={lodgingData.description}/>
            <Dropdown title={`Ã‰quipements`} details={<EquipmentsUl>{Equipments && Equipments.map(equipment => <li key={`equipment-${equipment}`}>{equipment}</li>)}</EquipmentsUl>}/>
            </InfosSection>
        </main>
    )}
}

export default Lodging
