import { Link } from 'react-router-dom'
import '../styles/Home.css'
import Card from '../components/Card'
import { Component } from 'react'

class Home extends Component{
    constructor(props) {
    super(props)
    this.state = {
        logementData: [],
    }
}

    componentDidMount() {
    // const { id } = this.props.match.params

    fetch(`logements.json`)
    .then((response) => response.json())
    .then((jsonResponse) => {
        this.setState({ logementData: jsonResponse })
        console.log(jsonResponse)
    })
    }

    render(){
        const logementData = this.state.logementData
    return (
        <main>
            <section className='home'>
                <h2 className='home__title'>Chez vous, partout et ailleurs</h2>
            </section>
            <section className='home__grid'>
                {logementData.map((logement, index) => (
                    <Link key={`${logement.title}-${index}`} to='/lodging'>
                    <Card />
                    <article className='home__grid__article' style={{backgroundImage: `url(${logement.cover})`}}>
                        <h3>{logement.title}</h3>
                    </article>
                    </Link>
                ))}
            </section>
        </main>
    )}    
}

export default Home
